INSERT INTO `product` VALUES (8, 'Rất đẹp mà', 'https://msmobile.com.vn/images/products/2022/01/28/large/10s_1643345070.png', 'Redmi Note 10S ĐGW (8GB/98GB)', 5690000, 'Redmi Note 10S ĐGW (8GB/128GB)', 29, 1, 94, 10);
INSERT INTO `product` VALUES (18, 'iPhone 12 Pro - \"Siêu phẩm công nghệ\" với nhiều nâng cấp mạnh mẽ về thiết kế, cấu hình và hiệu năng, khẳng định đẳng cấp thời thượng trên thị trường smartphone cao cấp.\r\nMàn hình giải trí OLED ấn tượng\r\nVẫn giống như thế hệ iPhone 11 Pro và iPho', 'https://cdn.tgdd.vn/Products/Images/42/213032/iphone-12-pro-vang-dong-new-600x600-1-600x600.jpg', 'Điện thoại iPhone 12 Pro 128GB', 29490000, 'Bảo hành chính hãng điện thoại 12 tháng', 28, 2, 97, 5);
INSERT INTO `product` VALUES (19, 'iPhone 13 mini được Apple ra mắt với hàng loạt nâng cấp về cấu hình và các tính năng hữu ích, lại có thiết kế vừa vặn. Chiếc điện thoại này hứa hẹn là một thiết bị hoàn hảo hướng tới những khách hàng thích sự nhỏ gọn nhưng vẫn giữ được sự mạnh mẽ bên tron', 'https://cdn.tgdd.vn/Products/Images/42/236780/iphone-13-mini-blue-2-600x600.jpg', 'Điện thoại iPhone 13 mini 128GB', 19990000, 'Bộ sản phẩm gồm: Hộp, Sách hướng dẫn, Cây lấy sim, Cáp Lightning - Type C', 28, 2, 98, 10);
INSERT INTO `product` VALUES (20, 'Xiaomi Mi 11 Lite là phiên bản thu gọn của Xiaomi Mi 11 5G được ra mắt trước đó. Thừa hưởng nhiều ưu điểm của đàn anh, Mi 11 Lite hoàn toàn có thể đáp ứng tốt các tác vụ thông thường một cách dễ dàng và đặc biệt hơn máy có thiết kế vô cùng mỏng nhẹ và thờ', 'https://cdn.tgdd.vn/Products/Images/42/233241/xiaomi-mi-11-lite-4g-blue-600x600.jpg', 'Điện thoại Xiaomi Mi 11 Lite', 7990000, 'Bảo hành chính hãng điện thoại 18 tháng', 29, 2, 89, 5);
INSERT INTO `product` VALUES (17, 'Cách đây không lâu, một số hình ảnh của chip xử lý A11 đã bị rò rỉ. Theo đó, các chân tiếp xúc của chip xử lý A11 khớp với bảng mạch iPhone 7s Plus vừa tiết lộ. Chip A11 của Apple được sản xuất trên công nghệ FinFET 10nm, bởi nhà sản xuất TSMC và chắc chắ', 'https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-xam-new-600x600-600x600.jpg', 'iPhone 7 Quốc tế', 2990000, '1 đổi 1 trong 30 ngày khi có lỗi phần cứng (đổi máy mới)', 28, 2, 98, 5);
INSERT INTO `product` VALUES (16, 'iPhone 12 Pro Max 128 GB một siêu phẩm smartphone đến từ Apple. Máy có một hiệu năng hoàn toàn mạnh mẽ đáp ứng tốt nhiều nhu cầu đến từ người dùng và mang trong mình một thiết kế đầy vuông vức, sang trọng.\r\nThay đổi thiết kế mới sau 6 năm\r\nTheo chu kỳ cứ ', 'https://msmobile.com.vn/images/products/2021/10/30/resized/iphone-13-pro-chinh-hang-vn-a_1635584041.png', 'Điện thoại iPhone 12 Pro Max 128GB ', 31990000, 'Phiên bản chính hãng mới 100% VN/A', 28, 2, 95, 10);
INSERT INTO `product` VALUES (21, 'iPhone 12 mini 64 GB tuy là phiên bản thấp nhất trong bộ 4 iPhone 12 series, nhưng vẫn sở hữu những ưu điểm vượt trội về kích thước nhỏ gọn, tiện lợi, hiệu năng đỉnh cao, tính năng sạc nhanh cùng bộ camera chất lượng cao.\r\nThiết kế sang trọng, cao cấp\r\nĐi', 'https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-mau-tim-3-600x600.jpg', 'Điện thoại iPhone 12 mini 64GB', 17490000, 'Điện thoại iPhone 12 mini 64GB', 28, 2, 89, 5);
INSERT INTO `product` VALUES (22, 'Redmi 9A là chiếc smartphone đến từ Xiaomi hướng tới nhóm khách hàng đang tìm kiếm cho mình một sản phẩm với cấu hình tốt giá thành phải chăng, cũng như được trang bị đầy đủ các tính năng ấn tượng.\r\nThiết kế hiện đại, thời thượng\r\nGiống như người anh Redm', 'https://cdn.tgdd.vn/Products/Images/42/218734/xiaomi-redmi-9a-grey-600x600-1-600x600.jpg', 'Điện thoại Xiaomi Redmi 9A', 2490000, 'Điện thoại Xiaomi Redmi 9A', 29, 2, 90, 7);
INSERT INTO `product` VALUES (23, 'Samsung đã chính thức giới thiệu chiếc điện thoại Galaxy A52s 5G đến người dùng, đây phiên bản nâng cấp của Galaxy A52 5G ra mắt cách đây không lâu, với ngoại hình không đổi nhưng được nâng cấp đáng kể về thông số cấu hình bên trong.\r\nThiết kế đặc trưng G', 'https://cdn.tgdd.vn/Products/Images/42/231110/Galaxy-S22-Black-600x600.jpg', 'Điện thoại Samsung Galaxy A52s 5G 128GB', 10990000, 'Điện thoại Samsung Galaxy A52s 5G 128GB', 30, 2, 90, 0);
INSERT INTO `product` VALUES (24, 'Samsung đã chính thức giới thiệu chiếc điện thoại Galaxy A52s 5G đến người dùng, đây phiên bản nâng cấp của Galaxy A52 5G ra mắt cách đây không lâu, với ngoại hình không đổi nhưng được nâng cấp đáng kể về thông số cấu hình bên trong.\r\nThiết kế đặc trưng G', 'https://cdn.tgdd.vn/Products/Images/42/247507/samsung-galaxy-a52s-5g-mint-600x600.jpg', 'Samsung Galaxy S21 FE 5G', 10990000, 'Samsung Galaxy S21 FE 5G', 30, 2, 92, 0);
INSERT INTO `product` VALUES (25, 'Samsung đã chính thức giới thiệu chiếc điện thoại Galaxy A52s 5G đến người dùng, đây phiên bản nâng cấp của Galaxy A52 5G ra mắt cách đây không lâu, với ngoại hình không đổi nhưng được nâng cấp đáng kể về thông số cấu hình bên trong.\r\nThiết kế đặc trưng G', 'https://cdn.tgdd.vn/Products/Images/42/242439/Galaxy-S22-Plus-White-600x600.jpg', 'Samsung Galaxy S22+ 5G', 12990000, 'Samsung Galaxy S22+ 5G', 30, 2, 100, 0);
INSERT INTO `product` VALUES (26, 'Sau sự thành công của Galaxy S21 series, Samsung tiếp tục tấn công phân khúc tầm trung với Galaxy A72 thuộc Galaxy A series, sở hữu nhiều màu sắc trẻ trung, hệ thống camera nhiều tính năng cũng như nâng cấp hiệu năng vô cùng lớn mang đến những trải nghiệm', 'https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-thumb-balck-600x600-600x600.jpg', 'Samsung Galaxy A72', 11990000, 'Samsung Galaxy A72', 30, 2, 98, 5);
INSERT INTO `product` VALUES (33, 'Nokia chính thức trình làng chiếc điện thoại Nokia 110 4G phiên bản nâng cấp của Nokia 110 2019 có điểm nhấn chính là công nghệ kết nối Internet 4G thỏa thích trải nghiệm mọi lúc, mọi nơi cùng với một thiết kế vô cùng mới mẻ và sang trọng.\nThiết kế sang trọng, bền bỉ\nNokia 110 4G sở hữu ngoại hình quen thuộc, khi sử dụng chất liệu nhựa Polycarbonate được tinh chỉnh giúp máy trở nên trẻ trung, năng động và vô cùng bắt mắt.\n\nĐiện thoại Nokia 110 4G | Thiết kế sang trọng, bền bỉ\n\nCác cạnh bên và phần nắp lưng máy cũng được bo tròn mềm mại tạo cảm giác cầm máy thoải mái không hề có bất kì chi tiết nhựa thừa hay ọp ẹp nào, dễ dàng thao tác sử dụng bằng một tay.\n\nĐương nhiên, đã là điện thoại Nokia thì độ bền của máy phải đạt chuẩn chất lượng châu Âu và bạn hoàn toàn có thể tháo được nắp lưng của máy để thay pin và thay SIM một cách dễ dàng.\n\nĐiện thoại Nokia 110 4G | Độ bền cao, đạt tiêu chuẩn chất lượng châu Âu\n\nNokia 110 4G đem tới người tiêu dùng nhiều màu sắc nổi bật bao gồm Vàng, Xanh và Đen để lựa chọn phù hợp với sở thích và cá tính của mỗi người.\n\nĐiện thoại Nokia 110 4G | Nhiều màu sắc lựa chọn\n\nMàn hình hiển thị tốt, nhiều tiện ích đi kèm\nMặt trước của Nokia 110 4G là màn hình kích 1.8 inch có độ phân giải QQVGA (120 x 160 Pixels) cho màu sắc chất lượng hình ảnh tốt, độ chi tiết của phông chữ và các biểu tượng được hiển thị sắc nét dễ nhìn dễ thao tác.\n\nĐiện thoại Nokia 110 4G | Màn hình hiển thị tốt\n\nNgoài ra, điện thoại còn được hỗ trợ khả năng đọc rõ các thông tin tiếng Việt có dấu giúp người dùng nắm bắt nội dung dễ dàng, cùng với đó là tính năng Zoomed menus giúp phóng to biểu tượng và điều hướng thao tác một cách nhanh chóng. \n\nĐiện thoại Nokia 110 4G | Thiết kế gọn nhẹ, dễ dàng cầm nắm\n\nNokia 110 4G được trang bị bàn phím T9 cổ điển có độ nảy cao, các phím bấm mềm mại và rất dễ thao tác đem đến sự thoải mái cho người dùng giúp tốc độ gõ văn bản được nhanh và chính xác hơn.\n\nĐiện thoại Nokia 110 4G | Bàn phím T9\n\nNokia 110 4G được trang bị camera phía sau có độ phân giải 0.08 MP với chất lượng đủ dùng, công cụ hiệu quả để lưu giữ tài liệu một cách dễ dàng mà không cần phải ghi chép.\n\nĐiện thoại Nokia 110 4G | Camera sau 0.08 MP\n\nNgoài ra, máy còn hỗ trợ đèn flash LED phía trên đỉnh máy có thể sử dụng được như đèn pin soi sáng, công cụ hữu hiệu trong những trường hợp cấp thiết.\n\nĐặc biệt, Nokia 110 4G còn hỗ trợ tính năng FM radio cho phép nghe mà không cần phải kết nối tai nghe. Hơn nữa, Nokia 110 4G có jack cắm tai nghe 3.5 mm, người dùng cũng có thể nghe nhạc thông qua WAV tích hợp sẵn. \n\nĐiện thoại Nokia 110 4G | Nghe FM Radio không cần tai nghe\n\n2 sim 2 sóng, hỗ trợ công nghệ mạng 4G chất lượng cao\nCung cấp sức mạnh cho máy là bộ vi xử lý Unisoc T107, RAM 128 MB và bộ nhớ trong 48 MB. Người dùng hoàn toàn có thể mở rộng dung lượng thông qua thẻ nhớ MicroSD tối đa 32 GB.\n\nĐiện thoại Nokia 110 4G | Hỗ trợ 2 sim 2 sóng 4G\n\nNgoài ra, máy cũng hỗ trợ 2 sim 2 sóng, nhờ đó việc liên lạc trở nên dễ dàng hơn, thuận tiện hơn và có thể luân phiên giữa hai số liên lạc. Rất phù hợp với những người làm nhiều công việc và cần sử dụng các số liên hệ khác nhau.\n\nBên cạnh đó, máy có khả năng kết nối trực tuyến cực nhanh với mạng 4G cho chất lượng cuộc gọi ổn định và trải nghiệm giải trí mượt mà. Cùng với đó là dịch vụ thoại chất lượng cao VoLTE mang đến cuộc gọi với âm thoại trong trẻo, rõ nét và vô cùng chân thực.\n\nĐiện thoại Nokia 110 4G | Thoải mái gọi điện liên lạc chất lượng cao\n\nThoải mái giải trí suốt ngày dài\nNgoài ra, Nokia 110 4G được trang bị viên pin rời dung lượng 1.020 mAh cho phép sạc qua cổng Micro USB, hứa hẹn cung cấp thời gian thoại lên đến 16 giờ ở chế độ 2G, 5 giờ thoại và thời gian chờ 12 ngày ở chế độ 4G.\n\nĐiện thoại Nokia 110 4G | Thoải mái giải trí cả ngày\n\nViên pin này cũng có thể tháo rời để thay thế viên pin dự phòng mới không làm gián đoạn khả năng sử dụng một cách dễ dàng.\n\nĐiện thoại Nokia 110 4G | Pin có thể tháo rời\n\nTạm kết, Nokia 110 4G là chiếc điện thoại với thiết kế nhỏ gọn nhưng mang trong mình vô vàn các tiện ích, ứng dụng đi kèm, đặc biệt còn hỗ trợ mạng 4G nhằm đem đến trải nghiệm tốt nhất cho người dùng.', 'https://cdn.tgdd.vn/Products/Images/42/240196/nokia-110-4g-yellow-600x600.jpg', 'Điện thoại Nokia 110 4G', 850000, 'Điện thoại Nokia 110 4G', 31, 3, 100, 0);
INSERT INTO `product` VALUES (34, 'Galaxy S22 Ultra 5G chiếc smartphone cao cấp nhất trong bộ 3 Galaxy S22 series mà Samsung đã cho ra mắt. Tích hợp bút S Pen hoàn hảo trong thân máy, trang bị vi xử lý mạnh mẽ cho các tác vụ sử dụng vô cùng mượt mà và nổi bật hơn với cụm camera không viền độc đáo mang đậm dấu ấn riêng.\nSở hữu một diện mạo đầy nổi bật\nGalaxy S22 Ultra 5G được kế thừa form thiết kế từ những dòng Note trước đây của hãng đem đến cho bạn có cảm giác vừa mới lạ vừa hoài niệm. Trọng lượng của máy khoảng 228 g cho cảm giác cầm nắm đầm tay, khi cầm máy trần thì hơi có cảm giác dễ trượt.\n\nGalaxy S22 Ultra 5G - Thiết kế\n\nPhần mặt lưng được làm nhám nên hạn chế tốt việc bám dấu vân tay, có thiết kế cũng khá đơn giản nhưng vẫn toát lên vẻ sang trọng và cao cấp của chiếc máy. Cụm camera sau trên Galaxy S22 Ultra 5G được thiết kế trần tạo cảm giác gọn gàng và cũng tạo nên một điểm nhấn độc đáo cho chiếc máy.\n\nGalaxy S22 Ultra 5G - Mặt lưng\n\nCó thể nói Galaxy S22 Ultra 5G chính là chiếc máy đầu tiên được tích hợp bút S Pen hoàn hảo trong một thân máy của dòng Galaxy S. Giờ đây, bạn có thể dễ dàng phác thảo, ghi chú lại những ý tưởng vô cùng nhanh chóng với độ trễ đã được cải thiện cho cảm giác viết vẽ vô cùng chân thật.\n\nGalaxy S22 Ultra 5G - Thao tác viết bằng bút S Pen\n\nCấu hình mạnh mẽ với Snapdragon 8 Gen 1\nHiệu năng trên Galaxy S22 Ultra 5G là điều khỏi bàn cãi khi máy sử dụng chip Snapdragon 8 Gen 1 vô cùng mạnh mẽ với tiến trình sản xuất 4 nm. Khi máy chơi game cũng chỉ ấm lên một chút ở vị trí cụm camera chứ không hoàn toàn quá nóng như những sản phẩm khác khi dùng cùng con chip này.\n\nMáy dễ dàng vượt qua được các tựa game Liên Quân, PUBG Mobile, cài đặt cấu hình tối đa thì biểu đồ FPS trên Galaxy S22 Ultra 5G vẫn khá ổn định, không bị drop quá nhiều. Kể cả tựa game Genshin Impact ở mức đồ họa mặc định thì máy chơi vẫn mượt mà, đồ họa hiển thị đẹp mắt.\n\nGalaxy S22 Ultra 5G - Trải nghiệm chơi game\n\nCòn nói về các tác vụ cơ bản như lướt web, đọc báo, xem YouTube,... thì máy hoàn thành một cách dễ dàng, sử dụng mượt mà, khả năng đa nhiệm thoải mái với RAM 8 GB.\n\nGalaxy S22 Ultra 5G - Trải nghiệm lướt web\n\nMang đến những bức ảnh đầy chất lượng\nMáy trang bị 4 camera với camera chính lên đến 108 MP, camera góc siêu rộng 12 MP, 2 camera tele 10 MP hỗ trợ thu phóng quang học 10x. Nổi bật nhất là camera chính với khả năng chụp đêm vô cùng ấn tượng như câu mà hãng đã đưa ra \"Nightography Camera - Mắt thần bóng đêm\". Sở hữu cảm biến điểm ảnh lớn nên thu được nhiều ánh sáng hơn, khi chụp đêm những bức ảnh cực kỳ sáng, hiện tượng nhiễu hạt được cải thiện đáng kể.\nGalaxy S22 Ultra 5G - Ảnh chụp trên camera\nChế độ zoom 100x hoạt động khá là ổn định, khả năng zoom xa và bắt nét ảnh nhanh. Tất nhiên khi bạn zoom tới 100x thì ảnh khá mờ, khoảng cách zoom để cho ra chất lượng ảnh tốt là khoảng 30-40x.\nGalaxy S22 Ultra 5G - Chế độ zoom 30x trên máy\nChế độ chụp chân dung cũng đã được hãng cải thiện khá nhiều so với thế hệ trước. Ảnh chụp xóa phông sâu, ít bị lẹm kể cả phần khó như tóc, độ chi tiết cao, ảnh tươi sáng và màu sắc khá chân thật. Bạn muốn chụp ảnh phong cảnh, khu phố hay đơn giản là chụp nhóm thì trên Galaxy S22 Ultra 5G hoàn toàn làm được với camera góc siêu rộng.\nGalaxy S22 Ultra 5G - Ảnh chụp trên camera\nGalaxy S22 Ultra 5G hỗ trợ quay video 8K tuy nhiên không đi kèm chống rung, nếu như bật chế độ Siêu ổn định lên thì độ phân giải sẽ bị giới hạn xuống Full HD 60 FPS. Bao nhiêu đó cũng khá đủ rồi, khung hình ổn định, ít bị giật, độ chi tiết cao và màu sắc cũng khá chân thật để chúng ta dễ dàng chỉnh sửa.\nCamera trước lên đến 40 MP cho nên hình sống ảo của chúng ta sẽ chân thật hơn bao giờ hết, da vẫn được làm mịn nhẹ nhưng các chi tiết trên da đều sẽ được tái tạo rất rõ nét. Màu sắc khá rực rỡ nên không cần áp dụng thêm bộ lọc màu nữa.\nTrải nghiệm xem nhìn hoàn hảo\nGalaxy S22 Ultra 5G trang bị một màn hình khá xuất sắc với độ phần 2K+, rộng 6.8 inch cùng với đó là tấm nền Dynamic AMOLED 2X đem đến một màu sắc hiển thị rực rỡ, màu đen sâu cho cảm giác xem nhìn chân thật. Hỗ trợ tần số quét 120 Hz nên khả năng vuốt chạm mượt mà, nhanh chóng. Màn hình có độ sáng cao 1750 nits nên việc khi dùng ngoài trời chất lượng hiển thị vẫn rất rõ ràng.\nGalaxy S22 Ultra 5G - Màn hình cong tràn sang 2 cạnh\nMặt kính cong tràn sang 2 cạnh khá sang trọng và cũng là đặc trưng của dòng Note, được trang bị kính cường lực Corning Gorilla Glass Victus+ gia tăng khả năng chống trầy xước rất tốt, cho bạn yên tâm hơn khi sử dụng máy.\nThời lượng sử dụng cả ngày với viên pin 5000 mAh\nViên pin trên Galaxy S22 Ultra 5G được hỗ trợ là 5000 mAh tương tự như Galaxy S21 Ultra 5G nhưng công suất sạc thì đã được nâng lên thành 45 W thay vì 25 W. Khả năng sạc nhanh ấn tượng với 30 phút được hơn 50% dung lượng và mất khoảng 1 giờ 10 phút là đầy.\nGalaxy S22 Ultra 5G - Thời gian sạc\nVì thế với dung lượng pin cao cùng khả năng sạc nhanh, thì Galaxy S22 Ultra 5G hoàn toàn có thể cùng bạn chinh phục các tựa game, những bộ phim yêu thích hoặc đáp ứng tốt nhu cầu làm việc cả một ngày dài.\nChuyển đổi dữ liệu nhanh chóng và tiện lợi\nNgoài ra, để trải nghiệm sử dụng của khách hàng được liền mạch hơn, Samsung có cung cấp một cách chuyển đổi dữ liệu an toàn, bảo mật với tính năng Smart Witch. Hỗ trợ người dùng truyền dữ liệu như ảnh, video, danh bạ, sự kiện lịch, ghi chú,... từ nhiều thiết bị kể cả khác hệ điều hành đến Samsung Galaxy S22 Ultra.\nBạn có thể tham khảo chi tiết bài hướng dẫn tại: Chuyển dữ liệu sang điện thoại Samsung bằng Smart Switch.\nTóm lại, với những ưu điểm như thiết kế sang trọng, camera chụp đêm ấn tượng, hiệu năng vượt trội thì Galaxy S22 Ultra 5G hoàn toàn xứng đáng để bạn bỏ tiền ra sở hữu em nó để phục vụ nhiều cầu sử dụng của bản thân.', 'https://cdn.tgdd.vn/Products/Images/42/235838/Galaxy-S22-Ultra-Burgundy-600x600.jpg', 'Điện thoại Samsung Galaxy S22 Ultra 5G 128GB ', 30990000, 'Điện thoại Samsung Galaxy S22 Ultra 5G 128GB ', 30, 3, 97, 0);
INSERT INTO `product` VALUES (36, 'Nokia G10 cùng với Nokia G20 là bộ đôi smartphone đầu tiên thuộc dòng G-series của Nokia, người dùng sẽ trải nghiệm lâu dài với dung lượng pin lớn, thiết kế thời trang và hoạt động trên hệ điều hành Android 11 nguyên bản, tối ưu sự mượt mà và hỗ trợ cập nhật đến 3 năm.\nMàn hình rộng hiển thị tuyệt vời từ nhiều góc nhìn\nTheo xu hướng chung, Nokia G10 trang bị màn hình tràn viền 6.5 inch, tối ưu bởi thiết kế giọt nước chứa camera selfie, phần viền trên và 2 bên được làm mỏng tối đa, cạnh dưới có phần dày hơn nhưng với cách đặt logo Nokia khéo léo giúp cho thiết kế trở nên cân đối và hài hòa.\n\nNokia G10 | Trang bị màn hình tràn viền 6.5 inch\n\nBằng việc sở hữu tấm nền TFT LCD cùng độ phân giải HD+, giúp cho hình ảnh có đủ độ sắc nét cần thiết để hiển thị nội dung rõ ràng, màu sắc đẹp với góc nhìn rộng, dễ dàng chia sẻ cho nhiều người cùng xem.\n\nCấu hình tốt cho nhu cầu cơ bản\nNokia G10 có thông số cấu hình khá cơ bản khi sử dụng vi xử lý Helio G25 với hiệu năng được tăng cường, xử lý nhanh và ổn định đáp ứng tốt nhu cầu liên lạc và giải trí của người dùng phổ thông.\n\nNokia G10 | Sử dụng vi xử lý Helio G25\n\nChip được sản xuất với tiến trình 12 nm, giúp cho Nokia G10 sử dụng ít năng lượng hơn và cho thời lượng sử dụng pin được kéo dài tối đa.\n\nNokia G10 | Chip được cải tiến giúp sử dụng ít năng lượng hơn và thời lượng pin được kéo dài\n\nMáy cũng đi kèm với RAM 4 GB và bộ nhớ trong 64 GB hỗ trợ đa nhiệm ổn định và lưu trữ dữ liệu ở mức cơ bản đủ dùng, bạn hoàn toàn có thể mở rộng thêm dung lượng lưu trữ với thẻ nhớ MicroSD lên đến 512 GB.\n\nNokia G10 | RAM 4 GB và bộ nhớ trong 32 GB hỗ trợ đa nhiệm ổn định và lưu trữ dữ liệu\n\nNgoài ra, Nokia G10 hoạt động trên Android 11 thuần Google cho sự mượt mà và đầy đủ những tính năng mới, hỗ trợ 2 năm nâng cấp lên phiên bản Android mới và 3 năm cập nhật bảo mật đến từ Nokia.\n\nNokia G10 | Hoạt động trên Android 11 thuần Google mượt mà và đầy đủ những tính năng mới\n\nThời lượng pin được tăng cường bởi AI\nNokia G10 cung cấp dung lượng pin khủng 5050 mAh, được tối ưu hoá bởi thuật toán AI tăng cường năng lượng cho trải nghiệm lâu, liền mạch hơn, bạn có thể giải trí, làm việc trong nhiều giờ mà không lo tình trạng cúp nguồn.\n\nNokia G10 | Pin dung lượng khủng 5050 mAh\n\nNokia G10 hỗ trợ sạc kèm theo máy 10 W thông thường nhưng vẫn đáp ứng tốt cho nhu cầu cơ bản và bảo vệ tuổi thọ cho pin được tốt hơn.\n\nBộ 3 camera lưu lại những khoảnh khắc\nNokia G10 trang bị bộ 3 camera sau gồm camera chính 13 MP và 2 camera phụ lần lượt là camera chiều sâu 2 MP và camera macro 2 MP giúp bạn ghi lại những khoảnh khắc đáng nhớ và khám phá những điều mới lạ trong cuộc sống xung quanh.', 'https://cdn.tgdd.vn/Products/Images/42/235995/Nokia%20g10%20xanh%20duong-600x600.jpg', 'Điện thoại Nokia G10', 3490000, 'Điện thoại Nokia G10', 31, 3, 100, 0);
INSERT INTO `product` VALUES (39, 'Reno6 Z 5G đến từ nhà OPPO với hàng loạt sự nâng cấp và cải tiến không chỉ ngoại hình bên ngoài mà còn sức mạnh bên trong. Đặc biệt, chiếc điện thoại được hãng đánh giá “chuyên gia chân dung bắt trọn mọi cảm xúc chân thật nhất”, đây chắc chắn sẽ là một “siêu phẩm\" mà bạn không thể bỏ qua.Bộ 3 camera chuyên nghiệp - Mỗi cảm xúc, một chân dung.Hệ thống camera sau được trang bị tối tân, trong đó có camera chính 64 MP, camera góc siêu rộng 8 MP và camera macro 2 MP cùng camera trước 32 MP luôn sẵn sàng bắt trọn mọi cảm xúc trong khung hình, giúp người dùng thoải mái ghi lại những khoảnh khắc trong cuộc sống một cách ấn tượng nhất.', 'https://cdn.tgdd.vn/Products/Images/42/236186/oppo-reno6-5g-black-600x600.jpg', 'Điện thoại OPPO Reno6 Z 5G', 7990000, 'Điện thoại OPPO Reno6 Z 5G', 44, 3, 4, 6);
INSERT INTO `product` VALUES (38, 'Sau khi ra mắt OPPO Reno5 chưa lâu thì OPPO lại cho ra mẫu smartphone mới mang tên OPPO Reno6 với hàng loạt cải tiến mới về ngoại hình bên ngoài lẫn hiệu năng bên trong, mang đến trải nghiệm vượt bật cho người dùng.\nChip Dimensity 900 5G hiệu năng ổn định\nOPPO Reno6 được trang bị vi xử lý MediaTek Dimensity 900 đáp ứng mọi nhu cầu giải trí hàng ngày, có thể chơi tất cả các game phổ biến hiện nay, tạo ấn tượng rất mạnh mẽ cho người dùng trải nghiệm mượt mà với cấu hình ổn định.\n\nTrang bị vi xử lý MediaTek Dimensity 900 - OPPO Reno6\n\nMáy hỗ trợ hệ điều hành Android 11 được cải tiến với nhiều tính năng vượt trội. Đi kèm đó là RAM 8 GB cho đa nhiệm thoải mái nhiều ứng dụng cùng một lúc. Bộ nhớ trong 128 GB cho bạn thoải mái tải những tệp nặng và những tựa game yêu thích dễ dàng hơn.\n\nDung lượng ROM và RAM - OPPO Reno6\n\nTheo xu hướng hiện nay thì OPPO Reno6 cũng được trang bị kết nối mạng 5G hiện đại với tốc độ truyền tải dữ liệu nhanh hơn gấp nhiều lần 4G, tải mọi game và ứng dụng nặng đều không thể làm khó được sự mạnh mẽ này.\n\nHỗ trợ kết nối 5G hiện đại - OPPO Reno6\n\nTrải nghiệm thú vị cùng những bộ ảnh cực chất\nMặt sau của OPPO Reno6 sở hữu cụm 3 camera bao gồm: Cảm biến chính 64 MP, camera góc siêu rộng 8 MP, camera macro 2 MP giúp người dùng thoải mái sử dụng ghi lại những khoảnh khắc hàng ngày.\n\nCụm 3 camera sau - OPPO Reno6\n\nThiết bị cũng hỗ trợ nhiều chế độ chụp ảnh thú vị khác như, HDR, chụp xóa phông, quay phim 4K, hỗ trợ quay video Full HD,... đáp ứng tốt nhu cầu sử dụng thông thường.\n\nHỗ trợ nhiều chế độ chụp khác nhau - OPPO Reno6\n\nMặt trước còn được trang bị camera selfie có độ phân giải 32 MP,  hỗ trợ tính năng tự động lấy nét và nhận diện khuôn mặt thông minh giúp người dùng có những bức ảnh chụp trọn vẹn và đẹp mắt nhất.\n\nẢnh chụp camera selfie sắc nét - OPPO Reno6\n\nThiết kế thời thượng, đẳng cấp\nVẻ ngoài mảnh mai và thon gọn đến bất ngờ với trọng lượng chỉ 182 g cho bạn cầm nắm thoải mái, bỏ túi hay cặp đều tiện lợi.\n\nThiết kế mảnh mai và thon gọn - OPPO Reno6\n\nReno6 có điểm nổi bật so với thế hệ trước là thiết kế nguyên khối vuông vức khá giống với iPhone 12, mặt sau của máy được phủ một lớp nhám mờ cho độ bám vân tay ít hơn, không bị trơn trượt trong quá trình sử dụng, tạo nên sự sang trọng khi mang theo bên mình. \n\nNgôn ngữ thiết kế vuông vức - OPPO Reno6\n\nGiải trí sống động màn hình Full HD+\nSử dụng công nghệ màn hình AMOLED với kích thước 6.43 inch cùng độ sáng tối đa 750 nits cho tái tạo màu và độ chân thực cao hơn, hình ảnh nét hơn, độ tương phản cao giúp người dùng có thể sử dụng điện thoại dưới mọi điều kiện sáng và góc nhìn rộng hơn đáng kể. \n\nMàn hình 6.43 inch được sử dụng công nghệ AMOLED - OPPO Reno6\n\nMặt kính của OPPO Reno6 được trang bị kính cường lực Corning Gorilla Glass 5 chịu áp lực cao khi va chạm vào bề mặt cứng cho bạn yên tâm hơn khi bỏ cặp hay túi quần.\n\nHỗ trợ bảo mật vân tay dưới màn hình cho phép người dùng chỉ cần thao tác một chạm nhẹ nhàng để mở khóa màn hình, nhanh chóng và  tiện lợi mà không ảnh hưởng đến vẻ thẩm mỹ của chiếc smartphone.\n\nBảo mật an toàn với cảm biến vân tay - OPPO Reno6\n\nNgoài ra, màn hình tần số quét  90 Hz và tốc độ lấy mẫu cảm ứng từ 180 Hz, mang đến độ mượt mà trong trải nghiệm.\n\nTần số quét 90 Hz cho hình ảnh mượt mà - OPPO Reno6\n\nCả ngày tràn đầy năng lượng\nReno6 sở hữu cho mình viên pin tương đối khủng, dung lượng 4300 mAh. Như vậy việc sử dụng để quay phim, chụp ảnh liên tục lên đến nhiều tiếng không phải là quá khó khăn với chiếc máy này.\n\nDung lượng pin 4300 mAh - OPPO Reno6\n\nMáy còn được trang bị công nghệ sạc nhanh 65 W, thời gian sạc siêu nhanh chỉ với vài chục phút, thả ga sử dụng trong một thời gian dài mà đợi không quá lâu.\n\nTóm lại, chỉ với giá tầm trung bạn đã có trong tay một chiếc điện thoại cùng nhiều tính năng hấp dẫn, đáp ứng các nhu cầu khác nhau, hứa hẹn sẽ là mẫu Smartphone phù hợp với nhiều người tin dùng.', 'https://cdn.tgdd.vn/Products/Images/42/233460/oppo-reno5-5g-thumb-600x600.jpg', 'Điện thoại OPPO Reno6 5G ', 7990000, 'Điện thoại OPPO Reno6 5G ', 44, 3, 14, 0);
INSERT INTO `product` VALUES (40, 'Vivo V23e - sản phẩm tầm trung được đầu tư lớn về khả năng selfie cùng ngoại hình mỏng nhẹ, bên cạnh thiết kế vuông vức theo xu hướng hiện tại thì V23e còn có hiệu năng tốt và một viên pin có khả năng sạc cực nhanh.\nThiết kế mỏng nhẹ cùng màu sắc tinh tế\nVivo V23e vẫn giữ đặc điểm nổi bật của Vivo V Series với thiết kế mỏng 7.36 mm ấn tượng (ở phiên bản màu đen). Viền màn hình 2 cạnh bên có độ mỏng ở mức vừa phải, tuy nhiên thì phần cạnh dưới thì có dày hơn một chút.\nPhần khung cũng được vát phẳng và hoàn thiện từ nhựa nhưng khi chạm vào bạn sẽ khó phân biệt được đây là chất liệu gì, bởi nó được xử lý bằng một lớp phủ siêu mịn mang lại cảm giác tương tự như các sản phẩm được hoàn thiện từ khung kim loại.\nPhiên bản màu đen có mặt lưng phẳng với hiệu ứng tráng gương khi sử dụng sẽ gặp tình trạng bị bám dấu vân tay, màu xanh hồng được hoàn thiện bởi công nghệ Satin AG nên có khả năng hạn chế được tình trạng bám dấu vân tay.\nVivo V23e sẽ không trang bị jack 3.5 mm bởi thiết kế siêu mỏng, phím nguồn và tăng giảm âm lượng được đặt ở cạnh phải, cạnh trên có một micro phụ, cạnh dưới có khe cắm sim, mic thoại, cổng sạc cùng 1 loa ngoài. Khi mở khe sim bạn lưu ý bởi khe lấy sim và mic thoại được nằm gần nhau bạn có thể sẽ thao tác nhầm nếu không để ý.\nVivo V23e sử dụng camera trước có độ phân giải lên đến 50 MP, đây là độ phân giải cao nhất trong ngành smartphone đến thời điểm hiện tại (11/2021).\nCameras selfie  độ phân giải lớn nhất trong ngành smartphone đến thời điểm hiện tại - Vivo V23e\nHỗ trợ tính năng Lấy Nét Tự Động Theo Mắt cho giúp quá trình selfie trở nên dễ dàng và rõ ràng, cùng với tính năng Video Selfie Ổn Định Khuôn Mặt giúp khóa nét gương mặt của bạn và giữ cho chủ thể trong khung hình luôn ổn định.\nCamera selfie hỗ trợ lấy nét theo mắt - Vivo V23e\nCamera trước vẫn có thể làm đẹp cho bạn khi gọi video qua các ứng dụng bên thứ ba như Messenger, Zalo. Bạn có thể điều chỉnh được phần trăm ảnh hưởng của khả năng làm đẹp để phù hợp với nhu cầu sử dụng, điều này sẽ giúp bạn tự tin hơn trong lúc tán gẫu, làm việc.\nVivo V23e được trang bị ba camera gồm: Camera chính 64 MP, camera góc rộng 8 MP có thể chụp được những bức ảnh có góc nhìn 120 độ, cuối cùng là camera macro phân giải 2 MP. Với phần cứng này thì Vivo V23e cũng hỗ trợ khá nhiều chế độ chụp để cho bạn thoải mái sáng tạo.\nỞ điều kiện đủ sáng, Vivo V23e cho ra ảnh với chi tiết khá, tái tạo màu sắc chân thực không quá rực rỡ mà làm mất đi vẻ đẹp tự nhiên của chủ thể. Camera chính có độ phân giải cao nên khi zoom ảnh thì tình trạng vỡ cũng được hạn chế ở mức tối đa.\nẢnh chụp trong điều kiện ngược sáng mang lại chi tiết tốt ở vùng sáng và tối, nhưng nếu zoom lên thì sẽ gặp tình trạng noise, với nhu cầu lưu trữ hình ảnh và chia sẻ lên mạng xã hội thì đây là vẫn một  tấm ảnh chất lượng tốt.\nSử dụng chip của nhà MediaTek\nVivo V23e sử dụng chip Helio G96 cùng bộ nhớ RAM 8 GB + 4 GB RAM ảo nhờ công nghệ RAM Mở Rộng 2.0, như vậy là bạn sẽ có đến 12 GB RAM, thực tế trong quá trình sử dụng hằng ngày của mình thì mình thấy Vivo V23e không phải tải lại bất kỳ ứng dụng nào, thời gian phản hồi khi mình mở, chuyển đổi các ứng dụng đa nhiệm cũng rất nhanh.\nXem thêm: RAM ảo (Virtual RAM) trên smartphone là gì?\nSử dụng chip của MediaTek - Vivo V23e\nCPU Helio G96 mang lại một trải nghiệm khá ổn, thời gian khởi chạy, hiệu ứng chuyển cảnh giữa các ứng dụng cũng rất nhanh chóng và dứt khoát. \nMình đã chơi Liên Quân trên Vivo V23e, máy có thể mở cấu hình đồ họa tối đa ở mức FPS 30 cho tựa game này, trải nghiệm chơi mượt ở bản đồ 5vs5 FPS luôn ổn định ở mức 30 không bị tụt sâu, nhưng để tốt ưu thời lượng pin và hiệu năng nhất thì bạn có thể đặt mức cấu hình trung bình.', 'https://firebasestorage.googleapis.com/v0/b/uploadfile-a5ce9.appspot.com/o/dbgt2qh6ut7?alt=media&token=1f2c7e6f-0b18-4db0-9360-1d433c26734d', 'Điện thoại Vivo V23e ', 8290000, 'Điện thoại Vivo V23e ', 36, 3, 97, 0);
INSERT INTO `product` VALUES (41, 'Vivo tung ra chiếc điện thoại Vivo V20 (2021) là phiên bản nâng cấp của Vivo V20 ra mắt trước đó. Chiếc smartphone này được nâng cấp lên bộ xử lý mạnh mẽ hơn mà vẫn giữ được thiết kế siêu mỏng và cụm camera chụp đẹp đáng kinh ngạc.\nSở hữu thiết kế mỏng đẹp mắt\nVivo V20 (2021) nổi bật với thiết kế mỏng chỉ 7.38 mm, mặt lưng được hoàn thiện từ kính mờ cao cấp, giúp hạn chế bám dấu vân tay. Khung máy làm từ nhựa nhưng có chất lượng hoàn thiện cao, mang lại cảm giác cứng cáp, chắn chắn.\nVivo V20 (2021) | Thiết kế mỏng nhẹ, thời thượng\nỞ mặt lưng chứa cụm 3 camera nằm thu gọn trong khuôn hình chữ nhật cùng với đèn LED kép, logo Vivo sang trọng được đặt ở phía dưới, cạnh viền cũng được gia công tỉ mỉ mang lại cảm giác thoải mái khi sử dụng trong thời gian dài.\nVivo V20 (2021) |  Cạnh viền gia công tỉ mỉ\nMột điểm cộng đến từ Vivo V20 (2021) là máy vẫn hỗ trợ đầy đủ các cổng kết nối như jack tai nghe 3.5 mm, khe cắm thẻ nhớ mở rộng lên đến 256 GB.\nVivo V20 (2021) |  Hỗ trợ thẻ nhớ mở rộng dung lượng\nCông nghệ màn hình AMOLED sắc nét\nVivo V20 (2021) được trang bị tấm nền AMOLED cho chất lượng hình ảnh sống động, chân thực. Kết hợp với màn hình kích thước lên đến 6.44 inch sẽ mang đến cho người dùng những trải nghiệm hình ảnh bất tận từ giải trí cho đến công việc. \nVivo V20 (2021) | Công nghệ màn hình AMOLED 6.44 inch\nVivo V20 (2021) có thiết kế camera giọt nước phía trước giúp tăng diện tích hiển thị đáng kể, camera này cũng hỗ trợ tính năng mở khóa khuôn mặt. Mở khóa vân tay được đặt ngay phía dưới màn hình, giúp tăng cường độ bảo mật cho smartphone của bạn, ngoài ra còn giúp bật máy nhanh chóng, tiện lợi hơn.\nVivo V20 (2021) | Bảo mật vân tay\nMặt kính được bảo vệ cho màn hình này là kính cường lực Schott Xensation UP giúp tăng độ bền, hạn chế tối đa hậu quả của việc rơi vỡ, trầy xước.\nThông số máy ảnh tuyệt vời\nTrong lần xuất hiện này, Vivo tiếp tục không làm thất vọng người dùng yêu thích “tạo kiểu” khi vẫn giữ được độ phân giải xuất sắc của dàn máy ảnh. Máy ảnh chính 64 MP, máy ảnh góc siêu rộng 8 MP và máy ảnh macro 2 MP.\nVivo V20 (2021) | Camera chính độ phân giải 64 MP\nVới độ phân giải này Vivo V20 (2021) dễ dàng cho ra hình ảnh có chi tiết rõ ràng, sống động, siêu nét ngay cả khi zoom mạnh. Hỗ trợ các tính năng như chụp bokeh, phim đen trắng, chế độ chụp đêm cung cấp đầy đủ ánh sáng cần thiết cho mọi bức ảnh buổi tối thêm lung linh,...\nVivo V20 (2021) | Chế độ chụp đêm lung linh, siêu nét ngay cả khi zoom mạnh\nMáy hỗ trợ quay video độ phân giải lên đến 4K. Mang lại trải nghiệm màu sắc sống động và độ chi tiết cao. Cùng với bộ công cụ chỉnh sửa mạnh mẽ sẽ mang đến cho bạn những thước phim hoàn hảo nhất.\nVivo V20 (2021) | Giao diện chụp ảnh\nCamera selfie của Vivo V20 (2021) sở hữu chế độ tự động lấy nét, bạn không còn phải khó khăn khi giữ nét cho những cảnh đẹp hay các chủ thể đang chuyển động nữa. Giờ đây, chỉ cần nhấn đúp và máy ảnh Ultra HD 44 MP sẽ làm thay bạn.\nVivo V20 (2021) | Camera selfie tự động lấy nét độ phân giải 44 MP\nMạnh mẽ hơn với bộ xử lý đổi mới\nCung cấp sức mạnh cho máy là CPU Snapdragon 730 8 nhân có xung nhịp tối đa 2.2 GHz, là CPU thuộc nhóm cao cấp với nhiều tính năng tiên tiến. Đặc biệt là tính chuyên game của nó, sử dụng ít điện năng hơn, cải thiện hiệu suất đồ họa, người dùng yêu thích gaming sẽ có những trải nghiệm chơi game tuyệt vời trên Vivo V20 (2021).\nVivo V20 (2021) | Snapdragon 730 8 nhân mạnh mẻ\nĐược trang bị RAM 8 GB giúp máy hoạt động đa nhiệm một cách mượt, bộ nhớ trong 128 GB cho bạn không gian lưu trữ lớn, bạn có thể thoải mái tải phim, ứng dụng mà không lo về vấn đề thiếu bộ nhớ.\nCông nghệ sạc pin nhanh 33 W \nMáy sở hữu viên pin Li-Po dung lượng 4000 mAh. Giờ đây, bạn có thể tích hợp cả làm việc, học tập và giải trí cả ngày dài mà máy vẫn có khả năng đáp ứng tốt.\nVivo V20 (2021) | Dung lượng 4000 mAh đáp ứng cả ngày dài\nBên cạnh đó, sạc nhanh của Vivo V20 (2021) có tốc độ nạp pin siêu tốc lên đến 33 W, giúp tiết kiệm tối đa thời gian sạc, thoải mái trải nghiệm mà không còn lo lắng về hết pin hay sạc lâu nữa!', 'https://firebasestorage.googleapis.com/v0/b/uploadfile-a5ce9.appspot.com/o/mz06stfh2gr?alt=media&token=622a8647-f1c4-4f01-9cd3-6877cb38f219', 'Điện thoại Vivo V20 (2021) ', 8490000, 'Điện thoại Vivo V20 (2021) ', 36, 3, 96, 0);
INSERT INTO `product` VALUES (42, 'Vivo cho ra mắt Vivo X70 Pro, một sản phẩm cao cấp ấn tượng với kiểu dáng lẫn hiệu năng tuyệt vời. Đặc biệt, camera còn được nâng cấp hàng loạt các tính năng thông minh, cùng bạn sáng tạo nên những kiệt tác đầy nghệ thuật.\r\nNgoại hình sang trọng, cao cấp hàng đầu\r\nVivo X70 Pro sở hữu ngoại hình bắt mắt, sang trọng của một chiếc smartphone cao cấp với  khung kim loại cứng cáp và các góc cạnh được bo cong mềm mại nhẹ trông khá nam tính và mạnh mẽ.\r\n\r\nĐiện thoại Vivo X70 Pro | Thiết kế sang trọng cao cấp hàng đầu\r\n\r\nVivo X70 Pro có khối lượng tương đối nhẹ với khoảng 184 g, mặt trước và mặt sau được xử lý cong 3D cho cảm giác chắc chắn thoải mái khi cầm nắm đồng thời giúp chiếc máy trở nên sang chảnh và đẳng cấp hơn.\r\n\r\nĐiện thoại Vivo X70 Pro | Thiết kế cao cấp\r\n\r\nBên cạnh đó, phần mặt lưng của Vivo X70 Pro được hoàn thiện dạng nhám mờ giúp bạn hạn chế việc bị bám mồ hôi và dấu vân tay, mặt lưng này còn có hiệu ứng chuyển màu khi nghiêng rất đẹp mắt.\r\n\r\nĐiện thoại Vivo X70 Pro | Mặt lưng nhám mờ với hiệu ứng màu ấn tượng\r\n\r\nCụm 4 camera nhiều đột phá\r\nĐiện thoại Vivo X70 Pro được trang bị cụm camera hợp tác cùng ZEISS, công ty sản xuất ống kính nổi tiếng hàng đầu của Đức, bao gồm camera chính 50 MP, camera tiềm vọng 12 MP, camera tele 8 MP và cuối cùng là một ống kính siêu rộng 12 MP.\r\n\r\nĐiện thoại Vivo X70 Pro | Cụm camera nhiều đột phá chất lượng \r\n\r\nỐng kính chính với độ phân giải cao chắc chắn sẽ cho chất lượng ảnh chụp cực sắc nét nhờ khả năng lấy nét vượt trội trong điều kiện thiếu sáng, nay tích hợp thêm công nghệ chống rung quang học OIS ấn tượng, chắc chắn sẽ hỗ trợ rất nhiều cho người dùng trong quá trình quay phim, chụp ảnh.\r\n\r\nĐiện thoại Vivo X70 Pro | Ảnh chụp điều kiện thiếu sáng\r\n\r\nCamera góc rộng và camera tele với khả năng chụp zoom xa 5x tối đa lên tới 30x, cho bạn tự do khám phá các khung cảnh ở xa mà không phải di chuyển. Từ đó có thể dễ dàng lưu giữ những khung cảnh tuyệt vời một cách rõ nét như ngay trước mắt bạn.\r\n\r\nĐiện thoại Vivo X70 Pro | Ảnh chụp từ camera\r\n\r\nTích hợp tính năng chống rung gyro-EIS, HDR10+ và nhiều chế độ chụp ảnh ấn tượng khác nữa. Đây sẽ là một trợ thủ đắc lực giúp bạn ghi lại những khoảnh khắc ấn tượng trong cuộc sống một cách mượt mà hơn, đặc biệt là các bạn thích làm vlog.\r\n\r\nCamera selfie của máy có độ phân giải cao lên đến 32 MP, hỗ trợ đầy đủ các tính năng chụp ảnh, làm đẹp, HDR và quay video Full HD thể tha hồ chụp ảnh tự sướng, quay video TikTok để chia sẻ lên mạng xã hội.\r\n\r\nĐiện thoại Vivo X70 Pro | Ảnh chụp từ camera\r\n\r\nMàn hình lớn, tần số quét 120 Hz mượt mà\r\nVivo X70 Pro được trang bị màn hình lên tới 6.56 inch, thiết kế tràn viền với đục lỗ được tối ưu với kích thước khá nhỏ và các viền benzel siêu mỏng mang lại một không gian hiển thị rộng lớn trên 90%.\r\n\r\nĐiện thoại Vivo X70 Pro | Màn hình giải trí 120Hz\r\n\r\nMàn hình Vivo X70 Pro sử dụng tấm nền AMOLED với độ phân giải Full HD+ (1080 x 2376 Pixels) mang lại một khả năng hiển thị sống động, chi tiết sắc nét bắt mắt cùng màu đen sâu rất thích hợp cho việc đọc báo, giải trí hay chơi game.\r\n\r\nMàn hình này sẽ càng tuyệt vời hơn nữa khi hỗ trợ tần số quét 120 Hz mang đến sự mượt mà vượt trội, người dùng có thể dễ dàng thấy rõ ở các tác vụ như hoạt cảnh hay cuộn trang.\r\n\r\nĐiện thoại Vivo X70 Pro | Màn hình hỗ trợ tần số quét 120 Hz\r\n\r\nĐể nâng cao trải nghiệm giải trí và sử dụng, Vivo X70 Pro còn được trang bị hệ thống loa có âm lượng và chất lượng vượt trội, mang lại trải nghiệm sống động cho bạn thỏa thích đắm chìm trong không gian giải trí âm nhạc hay xem video.\r\n\r\nĐiện thoại Vivo X70 Pro | Hệ thống âm thanh chất lượng cao\r\n\r\nHiệu năng vượt trội với chip MediaTek Dimensity 1200\r\nMẫu flagship hàng đầu của Vivo được trang bị một cấu hình khủng với con chip MediaTek Dimensity 1200 mạnh mẽ, cho khả năng xử lý các tác vụ một cách trơn tru, dư sức chiến tốt những tựa game nặng hàng đầu hiện nay như PUBG Mobile, Call of Duty: Mobile một cách mượt mà.\r\n\r\nĐiện thoại Vivo X70 Pro | Hiệu năng mạnh mẽ hàng đầu\r\n\r\nKết hợp với bộ vi xử lý là dung lượng RAM khủng lên tới 12 GB giúp bạn xử lý tác vụ đa nhiệm được mượt hơn, chạy được nhiều ứng dụng cùng lúc hơn và hạn chế được tối đa tình trạng giật hay đơ máy. \r\n\r\nĐiện thoại Vivo X70 Pro | Hiệu năng ấn tượng chiến game mượt mà\r\n\r\nBộ nhớ trong chuẩn UFS 3.1 lên đến 256 GB có tốc độ đọc, ghi vượt trội cho bạn tha hồ lưu trữ nhiều tài liệu cũng như tải các ứng dụng, game giải trí yêu thích.\r\n\r\nVivo X70 Pro sẽ chạy trên hệ điều hành Android 11 cùng giao diện Funtouch OS 12 thân thiện, các biểu tượng được làm mới với thiết kế tối giản, trải nghiệm của bạn sẽ trở nên tuyệt vời hơn bao giờ hết.\r\n\r\nDung lượng pin lớn, hỗ trợ sạc nhanh 44 W vượt trội\r\nVới X70 Pro, Vivo mang tới cho người dùng thời gian sử dụng khá ấn tượng, máy có thể sử dụng suốt cả ngày dài nhờ viên pin 4450 mAh. \r\n\r\nĐiện thoại Vivo X70 Pro | Thời lượng pin đủ dùng cho ngày dài\r\n\r\nĐiện thoại cũng trang bị công nghệ sạc nhanh 44 W trên cổng sạc Type-C tiện lợi giúp rút ngắn thời gian sạc đầy pin một cách tối đa, đảm bảo năng lượng luôn tràn đầy cho nhu cầu sử dụng hằng ngày.\r\n\r\nĐiện thoại Vivo X70 Pro | Khả năng sạc nhanh 44W\r\n\r\nTạm kết, Vivo X70 Pro thực sự là một sản phẩm rất đáng tiền trong tầm giá khi sở hữu thiết kế sang trọng, cấu hình ngon cùng cụm 4 camera ấn tượng, smartphone này hứa hẹn sẽ dễ dàng chinh phục nhiều đối tượng người dùng đặc biệt là những ai đam mê chụp ảnh.', 'https://cdn.tgdd.vn/Products/Images/42/248099/vivo-x70-pro-xanh-hong-1-600x600.jpg', 'Điện thoại Vivo X70 Pro 5G ', 19990000, 'Điện thoại Vivo X70 Pro 5G ', 36, 3, 95, 0);
INSERT INTO `product` VALUES (43, 'Thiết kế bóng bẩy, các chi tiết được hoàn thiện tốt\r\nTrên tay mình hiện tại là Vivo Y33s - chiếc điện thoại này có thiết kế khá tương đồng với các sản phẩm tầm trung mà Vivo cho ra mắt gần đây, vẫn là một sản phẩm có thiết kế trẻ trung với màu đen tráng gương và xanh mộng mơ. Ở phiên bản màu đen được phủ một lớp tráng gương sáng bóng, vào một số trường hợp nó có thể trở thành một chiếc gương soi tiện lợi, tuy nhiên khi sử dụng sẽ bị lưu lại vân tay khá rõ ràng.\r\n\r\nThiết kế trẻ trung | Vivo Y33s\r\n\r\nTổng quan máy khá nhẹ khi sở hữu trọng lượng chỉ 182 g, khung và mặt lưng được hoàn thiện từ nhựa, khung máy được vát phẳng và mình có thể đặt Y33s đứng được trên một mặt phẳng trông khá là thú vị. Các cạnh viền và cạnh góc của máy đã được thiết kế bo cong nhẹ, kết hợp cùng thiết kế mỏng 8 mm nên khi sử dụng sẽ không có cảm giác đau hay bị cấn tay.\r\n\r\nThiết kế mỏng nhẹ | Vivo Y33s\r\n\r\nXem bài đánh giá chi tiết thiết kế của Vivo Y33s: Trên tay Vivo Y33s - Thế Giới Di Động.\r\n\r\nHiệu năng ổn định với chip MediaTek\r\nVivo Y33s sử dụng chip Helio G80 cùng bộ nhớ RAM 8 GB, có thể mở rộng thêm 4 GB RAM nhờ công nghệ RAM Mở Rộng, như vậy là bạn sẽ có tổng 12 GB RAM. Mình có đo hiệu năng bằng phần mềm Antutu (bên trái) và PCMark (bên phải), đây là một con số khá ổn.\r\n\r\nKiếm tra bằng phần mềm đo hiệu năng | Vivo Y33s\r\n\r\nTrong quá trình sử dụng cơ bản của mình thì mình thấy Vivo Y33s cho thời gian phản hồi khi mở ứng dụng, chuyển đổi đa nhiệm qua lại giữa Facebook, TikTok, Liên Quân, Messenger... cũng rất nhanh.\r\n\r\nMình có chơi qua game Liên quân thì Y33s có thể cài đặt mức đồ họa cao, khi chơi 3 - 4 trận thì máy có tình trạng ấm dần lên, nhiệt độ tỏa đều không quá nóng tại một vị trí cố định, không gây khó chịu trong lúc bạn chơi game.\r\n\r\nChơi liên quân trên Vivo Y33s\r\n\r\nMàn hình thiết kế giọt nước, màu sắc hiển thị rực rỡ\r\nY33s tái tạo màu sắc tốt, màn hình này hiển thị hình ảnh khá rực rỡ, màu sắc rất tươi và nổi bật nên khi giải trí như xem phim thì sẽ mang lại một trải nghiệm tươi tắn, hấp dẫn.\r\n\r\nTrong phân khúc giá này thì các hãng hầu hết đã trang bị camera nốt ruồi trên các sản phẩm của họ mà Y33s vẫn sử dụng thiết kế giọt nước, nếu camera trước Y33s được thay thế bằng camera nốt ruồi thì sẽ ấn tượng hơn.\r\n\r\nMàn hình hiển thị rực rỡ | Vivo Y33s\r\n\r\nBảo vệ cho màn hình là một tấm kính cường lực Panda, với tấm kính này có thể giúp bạn yên tâm hơn trong quá trình sử dụng, Vivo cũng rất chu đáo khi trong hộp đã cung cấp một ốp lưng trong suốt và dán sẵn miếng dán màn hình cho sản phẩm.\r\n\r\nCamera mang lại chi tiết tốt trong môi trường đủ sáng\r\nCamera trên các sản phẩm tầm trung của Vivo luôn làm mình ấn tượng, ở điều kiện đủ sáng thì camera 50 MP này cho ra ảnh chụp có độ chi tiết cao, vùng sáng, vùng tối được tái tạo một cách chính xác khiến ảnh trở nên có chiều sâu hơn.\r\n\r\nẢnh chụp có chất lượng tốt ở môi trường đủ sáng | Vivo Y33s\r\nCamera trước 16 MP có nhiều tính năng hỗ trợ selfie, mang lại chất ảnh mịn màng, có thể làm hài lòng bạn trong các kiểu ảnh. Ngoài ra, camera trước có hỗ trợ chụp đêm, cho những \"chiếc\" ảnh ở môi trường thiếu sáng có chất lượng cao hơn.\r\n\r\nXem bài đánh giá chi tiết camera của Vivo Y33s: Đánh giá camera Vivo Y33s - Thế Giới Di Động.\r\n\r\nPin lớn thoải mái sử dụng ngày dài\r\nVivo Y33s được cung cấp năng lượng bởi viên pin 5000 mAh, viên pin này có thời gian sử dụng cực khủng khi máy có thể sử dụng liên lục lên đến hơn 8 tiếng với tác vụ hỗn hợp.\r\n\r\nCòn thời gian để nạp từ 0 - 100% pin với sạc công suất 18 W thì sẽ mất khoảng 2 tiếng 16 phút, đây là một khoảng thời gian ổn đối với viên pin lớn, nếu không sạc đầy bạn có thể sạc trước tầm 1/2 pin thì cũng có thể cung cấp thời gian sử dụng ổn trong 1 buổi với tác vụ nhẹ nhàng như mạng xã hội, nhắn tin.', 'https://cdn.tgdd.vn/Products/Images/42/249102/vivo-y33s-xanhhong-600x600.jpg', 'Điện thoại Vivo Y33s ', 6990000, 'Điện thoại Vivo Y33s ', 36, 3, 97, 0);
INSERT INTO `product` VALUES (44, '​Nokia 105 4G - Chiếc điện thoại phổ thông nổi bật với thiết kế sang trọng, khả năng nghe gọi bền bỉ trong thời gian dài, hỗ trợ công nghệ 4G cùng nhiều tiện ích giải trí hấp dẫn khác đến từ nhà Nokia.\r\nNgoại hình pha lẫn chút hiện đại\r\nToàn thân của máy được hoàn thiện từ nhựa với lớp vỏ sáng bóng nhìn khá đẹp mắt, tạo sự sang trọng và tinh tế cho máy. Một trong những điểm cộng của thiết bị là độ bền cao hạn chế được tình trạng nứt vỡ khi vô tình va chạm hoặc làm rơi.\r\n\r\nVẻ ngoài sáng bóng, cứng cáp - ​Nokia 105 4G\r\n\r\nƯu điểm tiếp theo của chiếc điện thoại này là mang cho mình tổng thể gọn nhẹ tiện lợi cho người dùng trong việc cất giữ, cầm nắm và di chuyển dễ dàng, mang đến cảm giác thoải mái khi sử dụng.\r\n\r\nThiết kế nhỏ gọn dễ dàng mang theo - ​Nokia 105 4G\r\n\r\nMáy sở hữu vẻ ngoài thời trang cùng với nhiều màu sắc trẻ trung và năng động, cho người sử dụng tha hồ lựa chọn để phù hợp với phong cách của bản thân.\r\n\r\nMàu sắc thời thượng - ​Nokia 105 4G\r\n\r\nKết nối 4G đột phá\r\nNokia 105 4G hoạt động trên con chip Unisoc T107 và RAM 128 MB cũng như bộ nhớ trong 48 MB, đáp ứng ổn các cho các tác vụ nghe gọi, sử dụng cơ bản. \r\n\r\nHoạt động trên con chip Unisoc T107 - ​Nokia 105 4G\r\n\r\nTuy là thiết bị phổ thông nhưng máy vẫn cung cấp công nghệ mạng 4G mạnh mẽ, bên cạnh đó dịch vụ thoại VoLTE lọc tạp âm và giảm nhiễu ồn, giúp người dùng có thể gọi ở những nơi đông người mà hạn chế nghe được tiếng nền xung quanh, mang tới chất lượng tuyệt hảo cho các khách hàng có nhu cầu cao về gọi điện.\r\n\r\nHiện đại với kết nối 4G - ​Nokia 105 4G\r\n\r\nThỏa sức giải trí đa dạng\r\nNokia 105 4G không chỉ là một chiếc điện thoại mà nó còn là một đài FM, bạn có thể cập nhật thông tin qua đài FM được phát sóng trực tiếp mà không cần dùng tai nghe vô cùng tiện lợi.\r\n\r\nDễ dàng cập nhật thông tin qua đài FM - ​Nokia 105 4G\r\n\r\nĐa dạng hơn nữa khi được tích hợp trò chơi \"Rắn săn mồi\" huyền thoại và các tựa game cơ bản khác trên thiết bị mang đến cho bạn thêm một chọn lựa giải trí khi nghỉ ngơi.\r\n\r\nĐược tích hợp các trò chơi cơ bản - ​Nokia 105 4G\r\n\r\nNokia 105 4G hỗ trợ 2 sim 2 sóng liên lạc trở nên thuận tiện hơn với bạn bè, người thân, tiết kiệm kha khá chi phí nhờ hưởng được nhiều khuyến mãi hấp dẫn từ các nhà mạng. Hỗ trợ jack tai nghe 3.5 mm để bạn nghe nhạc vào lúc học tập hay làm việc mệt mỏi.\r\n\r\nHỗ trợ 2 sim 2 sóng - ​Nokia 105 4G\r\n\r\nThời gian hoạt động bền bỉ\r\nMáy được trang bị viên pin 1020 mAh, tương đối lớn đối với điện thoại phổ thông, bạn có thể sử dụng liên tục khoảng 3-5 ngày thoải mái tùy vào tần suất sử dụng.\r\n\r\nTrang bị viên pin 1020 mAh - ​Nokia 105 4G\r\n\r\nViệc nạp năng lượng cho thiết bị cũng vô cùng đơn giản nhờ chuẩn sạc Micro USB phổ biến.\r\n\r\nNokia 105 4G, chiếc điện thoại mang đến nhiều giá trị cho người dùng, về công nghệ 4G hiện đại hay các nhu cầu liên lạc hoặc thậm chí là giải trí cơ bản, thì Nokia 105 4G chắc chắn sẽ không làm thất vọng người tin dùng khi lựa chọn.', 'https://cdn.tgdd.vn/Products/Images/42/240194/nokia-105-4g-blue-600x600.jpg', 'Điện thoại Nokia 105 4G', 750000, 'Điện thoại Nokia 105 4G', 31, 3, 0, 0);
INSERT INTO `product` VALUES (45, 'Nokia 105 Single SIM (2019) là sự lựa chọn hàng đầu cho những người dùng muốn một chiếc điện thoại cục gạch với độ bền cao và tới từ thương hiệu Nokia vốn đã tạo được lòng tin từ người dùng.\r\nKiểu dáng hiện đại\r\nNokia 105 Single SIM (2019) được thiết kế từ chất liệu nhựa polycarbonate cho độ bền cao, chống chịu va đập hiệu quả.\r\n\r\nĐiện thoại Nokia 105 Single SIM (2019) | Thiết kế nnhựa polycarbonate độ bền cao\r\n\r\nNhờ được làm từ nhựa nên máy cũng có rất nhiều màu sắc sặc sỡ thoải mái cho bạn thể hiện cá tính riêng.\r\n\r\nĐiện thoại Nokia 105 Single SIM (2019) | Bàn phim T9 tiện lợi\r\n\r\nĐiện thoại này vẫn giữ được cho mình bộ bàn phím T9 quen thuộc mang lại cảm giác bấm rất thoải mái và nhắn tin dễ dàng.\r\n\r\nKích thước nhỏ gọn trong lòng bàn tay cũng giúp bạn dễ dàng mang bên mình mọi lúc mọi nơi.\r\n\r\nGiải trí đa phương tiện\r\nVới Nokia 105 Single SIM (2019) bạn cũng có thể cập nhật những thông tin mới nhất mọi lúc mọi nơi với ứng dụng đài FM được cài đặt sẵn.\r\n\r\nĐiện thoại Nokia 105 Single SIM (2019) | Cổng sạc Micro USB và đèn LED tiện lợi\r\n\r\nNhững lúc rảnh rỗi bạn cũng có thể thư giãn với trò chơi rắn săn mồi cũng như một số tựa game đơn giản khác.\r\n\r\nVới khả năng lưu trữ 2000 danh bạ cùng 500 tin nhắn thì bạn cũng có thể hoàn toàn yên tâm bỏ chiếc sim chính của mình vào trong máy.\r\n\r\nPin tốt đàm thoại cả ngày\r\nNokia 105 Single SIM (2019) sở hữu viên pin dung lượng 800 mAh giúp bạn có thể sử dụng nghe gọi, nhắn tin thông thường 2 - 3 ngày hay thời gian trò chuyện liên tục lên đến 14.4 tiếng mới cần sạc pin (theo thông số hãng).\r\n\r\nĐiện thoại Nokia 105 Single SIM (2019) | Viên Pin rời thời gian chờ lâu\r\n\r\nSạc pin của Nokia 105 Single SIM (2019) cũng rất đơn giản với cổng sạc Micro USB mà bạn có thể tìm thấy bất cứ đâu trong cuộc sống hiện nay.', 'https://cdn.tgdd.vn/Products/Images/42/211934/nokia-105-2019-black-600x600.jpg', 'Điện thoại Nokia 105 Single SIM', 440000, 'Điện thoại Nokia 105 Single SIM', 31, 3, 0, 0);
INSERT INTO `product` VALUES (47, 'Tại sự kiện ra mắt sản phẩm mới diễn ra hôm 29/3, Xiaomi đã ra mắt Xiaomi Redmi Note 11S 5G toàn cầu. Thiết bị là một bản nâng cấp đáng giá so với Redmi Note 11S 4G, cùng xem máy có gì đặc biệt thôi nào.\nThiết kế quen thuộc nhưng vẫn thời thượng\nRedmi Note 11S 5G có ngoại hình bắt mắt với 3 màu sắc thời thượng gồm: Midnight Black, Twilight Blue và Star Blue. Viền màn hình hai bên của máy khá mỏng cùng với đó là cạnh viền bo cong theo lòng bàn tay, tạo cảm giác cầm nắm thoải mái hơn.\nThiết kế thời thượng - Xiaomi Redmi Note 11S 5G\nMàn hình của Redmi Note 11S 5G có tấm nền IPS LCD với kích thước 6.6 inch cùng tốc độ làm tươi 90 Hz. Chưa hết, cụm camera thiết kế dọc tạo điểm nhấn khá ấn tượng cho mặt sau, người dùng cũng sẽ bắt gặp logo Redmi ở góc trái bên dưới của máy.\nHiệu năng ổn định với chip Dimensity 810 5G\nCác sản phẩm Xiaomi không bao giờ làm chúng ta thất vọng về cấu hình và Redmi Note 11S 5G cũng không ngoại lệ. Với con chip Mediatek Dimensity 810 5G, người dùng sẽ thoải mái chơi game, xem phim, lướt web và các tác vụ khác mà không lo giật lag.\nBạn cũng sẽ nhận được viên pin 5000 mAh đi kèm sạc nhanh 33 W, thông số này có thể giúp bạn sử dụng máy liên tục trong 1 ngày, thậm chí có thể lên đến 2 ngày nếu bạn sử dụng không thường xuyên.\nCamera phục vụ yêu cầu chụp ảnh cơ bản\nRedmi Note 11S 5G sở hữu cụm 3 camera sau, trong đó cảm biến chính có độ phân giải 50 MP, 1 camera siêu rộng 8 MP và camera macro 2 MP. Với cụm camera này, bạn có thể thoả thích ghi lại những khoảnh khắc thú vị trong cuộc sống.<hr>\nCamera phục vụ yêu cầu chụp ảnh cơ bản - Xiaomi Redmi Note 11S 5G\nRedmi Note 11S 5G là một thiết bị khá tốt với hiệu năng ổn định, thiết kế đẹp và đặc biệt có hỗ trợ 5G. Nhìn chung, đây sẽ là một lựa chọn thích hợp cho bạn ở phân khúc tầm trung.', 'https://cdn.tgdd.vn/Products/Images/42/272668/xiaomi-redmi-note-11s-5g-lam-hong-thumb-600x600.jpg', 'Điện thoại Xiaomi Redmi Note 11S 5G', 6490000, '1', 29, 24, 99, 5);
